import React from 'react';


class TvShow extends React.Component {
  render() {
    console.log(this);
    const catalog = {
      id: 4,
      name: "Friends",
      image:
        "https://i.ibb.co/C6GmthN/viagensdatalita-friends-seriadofriends-FRIENDS-s-rie-netflix-seriado-warner-seriado-friends25anos.jpg?w=144",
      characters: ["Monica", "Rachel", "Phoebe", "Ross", "Chandler", "Joey"],
      genre: "Comédia",
      info: {
        seasons: "10 temporadas", 
        episodes: 236
      }
    }
    return (
      <article>
            <h2>Título da Série:</h2>
            <img src={catalog.image} alt={ `Foto de capa da série ${catalog.name}` }/>
            <p>Gênero: {catalog.genre}</p>
            <p>Personagens: {catalog.characters.map((personagem) => {
              return <strong>{`${personagem}|`} </strong>
            })}</p>
            <p>Número de Episodios: <strong>{catalog.info.episodes}</strong> </p>
          </article>
    )
  }
}
export default TvShow;
