import React from 'react';


class TvShow extends React.Component {
  render() {
    // console.log(this.props);
    const {tvShow} = this.props;
    return (
      <article>
            <h2>Título da Série: {tvShow.name} </h2>
            <img src={tvShow.image} alt={ `Foto de capa da série ${tvShow.name}` }/>
            <p>Gênero: <strong>{tvShow.genre}</strong></p>
            <p>Personagens: {tvShow.characters.map((personagem) => {
              return <strong>{`${personagem}|`} </strong>
            })}</p>
            <p>Número de Temporadas: <strong>{tvShow.info.seasons}</strong> </p>
            <p>Número de Episodios: <strong>{tvShow.info.episodes}</strong> </p>
          </article>
    )
  }
}
export default TvShow;
