import React from 'react';
import catalog from '../data';

class TvShow extends React.Component {
  render() {

    return (
      <article>
            <h2>Título da Série:</h2>
            <img src={catalogo.image} alt={ `Foto de capa da série ${catalogo.name}` }/>
            <p>Gênero: {catalogo.genre}</p>
            <p>Personagens: {catalogo.characters.map((personagem) => {
              return <strong>{`${personagem}|`} </strong>
            })}</p>
            <p>Número de Episodios: <strong>{catalogo.info.episodes}</strong> </p>
          </article>
    )
  }
}
export default TvShow;
