import React from 'react';
import './data';


class Pokemon extends React.Component{
  render() {
    console.log(this.props);
    // const { cadaPokemon} = this.props
    const pokemons = {

        id: 25,
        name: "Pikachu",
        type: 'Electric',
        averageWeight: {
            value: 6.0,
            measurementUnit: "kg"
        },
        image: "https://cdn2.bulbagarden.net/upload/b/b2/Spr_5b_025_m.png",
        moreInfo: "https://bulbapedia.bulbagarden.net/wiki/Pikachu_(Pok%C3%A9mon)"
    }
    return (
      <section className="cadaPoke">
        <h1>Nome do Pokemon: {pokemons.name} </h1>
        <p>Tipo: {pokemons.type} </p>
        <p>Peso m√©dio: {`${pokemons.averageWeight.value} ${pokemons.averageWeight.measurementUnit}`} </p>
        <figure>
          <img src={pokemons.image} />
        </figure>
      </section>
    );
  }
}
export default Pokemon;
