  constructor() {
    super();
    this.state ={
      nome: '',
      email: '',
    }

  }
  render() {

    return(
      <main>
        <form>
        <section>
          <label>Nome
            <input name="" type="text" placeholder="Digite seu nome"/>
          </label>
          <label>Email
            <input name="" type="email" placeholder="Digite seu email"/>
          </label>
        </section>
        </form>
      </main>
    )
  }
}
export default Form;
